INSERT INTO `content_article` VALUES (291, '丹顶鹤怎样生儿育女？', '　　丹顶鹤的巢建在长有芦苇的浅水滩上，直径1米左右，裸露在外。每年4~5月间，丹顶鹤交配产蛋，每窝两枚，雌雄轮流孵化，一只孵蛋时，另一只在周围巡逻，直至“新生儿”问世', '2024-12-17 19:06:51', '2024-12-17 19:06:51', 'admin', 'admin', 0);
