INSERT INTO `content_article` VALUES (295, '为什么轮船的底部要涂上红色油漆？', '　　这些红色油漆是一些有毒物质，用来防止小海洋生物附在船底。因为船底积聚太多东西，会增加船的重量，减慢轮船的航行速度，还会损坏船壳。', '2024-12-17 19:06:54', '2024-12-17 19:06:54', 'admin', 'admin', 0);
